import styled from "styled-components";


const Container = styled.div`
flex:1;
margin:3px;
height:50vh;
position: relative;
border-radius:25px;
color: blue;
`;

const Image = styled.img`
  width:100%;
  height:80%;
  border: radius 200px;00px;
  object-fit: cover;
  border-radius:100px;
  
`;
const Info = styled.div`
position:absolute;
width:100%;
height:100%;
top:0;
left:0;
display: flex;
flex-direction: column;
align-items: center;
justify-content: center;

`;

const Title = styled.h1`
color: coral;
margin-bottom:20px;
`;

const Button = styled.button`
border: none;
padding:10px;
background-color: transparent;
color: #ee4d02;
cursor: pointer;
font-weight: 600;
`;

const CategoryItem = ({item}) => {
  return (
    <Container>
        <Image src={item.img}/>
        <Info>
        <Title>{item.title}</Title>
        <Button>SHOP HERE</Button>
        </Info>
    </Container>
  )
}

export default CategoryItem
