import styled from "styled-components"

const Container=styled.div`
    width:100vw;
    height:100vh;
    background: linear-gradient(
        rgba(255, 255, 255, 0.5),
      rgba(255, 255, 255, 0.5)
    ),
    url("https://images.pexels.com/photos/547114/pexels-photo-547114.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940")
     center;
     background-size:cover;
     display:flex;
     align-items:center;
     justify-content:center;
`;

const Wrapper=styled.div`
    width:40%;
    padding:20px;
    background-color:white;
`;

const Title=styled.h1`
    font-size:24px;
    font-weight:300;

`;

const Form=styled.form`
    display:flex;
    flex-wrap:wrap;
`;

const Input=styled.input`
    flex:1;
    min-width: 40%;
    margin: 20px 10px 0px 0px;
    padding:10px;
`;

const Agreement=styled.span`
    font-size:12px;
    margin:20px 0px;
`;

const Button=styled.button`
    width:40%;
    border:none;
    padding:15px 20px;
    background-color:teal;
    color:white;

`


const Register = () => {
  return (
   <Container>
    <Wrapper>
        <Title>Create Account</Title>
        <Form>
            <Input placeholder="name"/>
            <Input placeholder="last name"/>
            <Input placeholder="username"/>
            <Input placeholder="email"/>
            <Input placeholder="password"/>
            <Input placeholder="confirm password"/>
            <Agreement> creating this account confirms the consent of personal data in accordance with the <b>PRIVACY POLICY</b>
            </Agreement>
            <Button>CREATE</Button>
        </Form>
    </Wrapper>
   </Container>
  )
}

export default Register
